import styled from "styled-components";
import { StyledButton } from "../StyledButton/StyledButton";

export const StyledFavButton = styled(StyledButton)`
  all: unset;
  display: none;
  position: absolute;
  top: 16px;
  left: 16px;
  cursor: pointer;
  width: 204px;

  & .isFav {
    path {
      color: red;
    }
  }
`;
